// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./InsurancePool.sol";
import "./IDevParameters.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract InsuranceFactory is Ownable, IDevParameters {
  address public immutable poolImplementation;

  // in BPS unit
  uint256 public investorDevFee = 4000;
  uint256 public buyerDevFee = 200;
  uint256 public maxBuyerFee = 2000;
  
  constructor(address _poolImplementation) {
    poolImplementation = _poolImplementation;
  }

  event DeployPool(address indexed owner, address indexed pool, uint256 expiration);
  function deployPool(
    address _owner,
    IERC20 _baseToken,
    uint256 _buyPercentageFee,
    uint256 _depositPercentageFee,
    uint256 _depositLimit,
    uint256 _distributionRatio,
    uint256 _masterchefRatio,
    uint256 _expiration,
    string memory _tokenName,
    string memory _tokenSymbol
  ) public returns(InsurancePool pool) {
    pool = InsurancePool(Clones.clone(poolImplementation));
    pool.initialize(
      _owner, 
      _baseToken,
      _buyPercentageFee,
      _depositPercentageFee, 
      _depositLimit, 
      _distributionRatio, 
      _masterchefRatio, 
      _expiration, 
      _tokenName, 
      _tokenSymbol
    );

    emit DeployPool(_owner, address(pool), _expiration);
  }

  event UpdateInvestorDevFee(uint256 newFeeBps);
  function updateInvestorDevFee(uint256 feeBps) public onlyOwner {
    require(feeBps <= 10000, "Invalid fee");
    investorDevFee = feeBps;
    emit UpdateInvestorDevFee(feeBps);
  }

  event UpdateBuyerDevFee(uint256 newFeeBps);
  function updateBuyerDevFee(uint256 feeBps) public onlyOwner {
    // Limit to maxBuyerFee
    require(feeBps <= maxBuyerFee, "Invalid fee");
    buyerDevFee = feeBps;
    emit UpdateBuyerDevFee(feeBps);
  }

  event UpdateMaxBuyerFee(uint256 newFeeBps);
  function updateMaxBuyerFee(uint256 feeBps) public onlyOwner {
    require(feeBps <= 10000, "Invalid fee");
    maxBuyerFee = feeBps;
    emit UpdateMaxBuyerFee(feeBps);
  }
}

contracts/InsuranceMasterchef.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "./InsurancePool.sol";
import "./IDistribute.sol";

interface IMintableERC20 is IERC20 {
	function mint(address _to, uint256 _amount) external;
}

interface ITransferable {
	function transfer(address to, uint256 amount) external returns (bool);
}

// Note: Precaution not to add baseToken as a pool (not make any sense)
contract InsuranceMasterchef is ReentrancyGuardUpgradeable, IDistribute {
	using SafeERC20 for IERC20;

	// Info of each user.
	struct UserInfo {
		uint256 amount; // How many LP tokens the user has provided.
		uint256 rewardDebt; // Reward debt. See explanation below.
	}

	// Info of each pool.
	struct PoolInfo {
		IERC20 lpToken; // Address of LP token contract.
		uint256 allocPoint; // How many allocation points assigned to this pool. Percentage of reward to be distributed.
		uint256 lastRewardTimestamp; // Last block number that reward distribution occurs.
		uint256 accRewardPerShare; // Accumulated reward per share, times 1e12. See below.
		uint16 depositFeeBP; // Deposit fee in basis points
	}

	// The reward token
	IERC20 public baseToken;
  // Insurance pool address
	InsurancePool public insurancePool;
	// Deposit Fee address
	address public feeAddress;

	// Info of each pool.
	uint256[] public poolInfoPidList; // pid to massUpdatePool
	address[] public poolInfoDummyList;
	mapping(uint256 => PoolInfo) public poolInfo;
	// Info of each user that stakes LP tokens.
	mapping(uint256 => mapping(address => UserInfo)) public userInfo;
	// Total allocation points. Must be the sum of all allocation points in all pools.
	uint256 public totalAllocPoint;
	// Useless but copied from a masterchef implementation
	uint256 public startTimestamp;

	event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
	event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
	event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);
	event SetFeeAddress(address indexed user, address indexed newAddress);

  /**
   * @dev Tells the address of the owner
   * @return the address of the owner
   */
  function owner() public view returns(address) {
    return OwnableUpgradeable(insurancePool).owner();
  }

  modifier onlyOwner() {
    require(insurancePool.isGovernance(msg.sender), "Ownable: caller is not the owner");
    _;
  }

	function initialize(
    address _insurancePool,
		address _baseToken
	) external initializer {
    insurancePool = InsurancePool(_insurancePool);
		baseToken = IERC20(_baseToken);
		feeAddress = insurancePool.owner();
		startTimestamp = block.timestamp;

		__ReentrancyGuard_init();
	}

	function setStartTimestamp(uint256 newTimestamp) external onlyOwner {
		require(block.timestamp < newTimestamp && block.timestamp < startTimestamp, "already start");
		startTimestamp = newTimestamp;

		uint256 poolInfoPidListLength = poolInfoPidList.length;
		for (uint256 i = 0; i < poolInfoPidListLength; i++) {
			poolInfo[poolInfoPidList[i]].lastRewardTimestamp = newTimestamp;
		}
	}

	function poolLength() external view returns (uint256) {
		return poolInfoDummyList.length;
	}

	mapping(IERC20 => bool) public poolExistence;

	modifier nonDuplicated(IERC20 _lpToken) {
		require(poolExistence[_lpToken] == false, "duplicated");
		_;
	}

	// Add a new lp to the pool. Can only be called by the owner.
	function add(uint256 _allocPoint, IERC20 _lpToken, uint16 _depositFeeBP) external onlyOwner nonDuplicated(_lpToken) {
		require(_depositFeeBP <= 10000, "invalid deposit fee basis points");

		uint256 newPid = poolInfoDummyList.length;

		uint256 lastRewardTimestamp = block.timestamp > startTimestamp ? block.timestamp : startTimestamp;
		totalAllocPoint = totalAllocPoint + _allocPoint;
		poolExistence[_lpToken] = true;
		poolInfo[newPid] = PoolInfo({
		    lpToken: _lpToken,
		    allocPoint: _allocPoint,
		    lastRewardTimestamp: lastRewardTimestamp,
		    accRewardPerShare: 0,
		    depositFeeBP: _depositFeeBP
		});
		poolInfoDummyList.push(address(_lpToken));
		poolInfoPidList.push(newPid);
	}

	// emergency flag use when we add invalid token address to
	function remove(uint256 _pid, bool emergency) external onlyOwner {
		if (poolInfoPidList.length == 0) {
			return;
		}

		PoolInfo storage pool = poolInfo[_pid];
		require(address(pool.lpToken) != address(0), "not found");

		uint256 length = poolInfoPidList.length;
		for (uint256 i = 0; i < length; ++i) {
			if (poolInfoPidList[i] == _pid) {
				if (pool.allocPoint == 0 || emergency) {
					pool.allocPoint = 0;
					poolInfoPidList[i] = poolInfoPidList[length - 1];
					poolInfoPidList.pop();
				}
				break;
			}
		}
	}

	// Update the given pool's reward allocation point and deposit fee. Can only be called by the owner.
	function set(uint256 _pid, uint256 _allocPoint, uint16 _depositFeeBP) external onlyOwner {
		require(_depositFeeBP <= 10000, "invalid deposit fee basis points");

		uint256 prevAllocPoint = poolInfo[_pid].allocPoint;
		totalAllocPoint = (totalAllocPoint - prevAllocPoint) + _allocPoint;
		poolInfo[_pid].allocPoint = _allocPoint;
		poolInfo[_pid].depositFeeBP = _depositFeeBP;

		if (_allocPoint > 0 && prevAllocPoint == 0) {
			uint256 length = poolInfoPidList.length;
			bool found = false;
			for (uint256 i = 0; i < length; ++i) {
				if (poolInfoPidList[i] == _pid) {
					found = true;
					break;
				}
			}

			if (!found) {
				poolInfoPidList.push(_pid);
			}
		}
	}

	// View function to see pending reward on frontend.
	function pendingReward(uint256 _pid, address _user) external view returns (uint256) {
		PoolInfo storage pool = poolInfo[_pid];
		UserInfo storage user = userInfo[_pid][_user];
		uint256 accRewardPerShare = pool.accRewardPerShare;
		return ((user.amount * accRewardPerShare) / 1e12) - user.rewardDebt;
	}

	// Update reward variables for all pools. Be careful of gas spending!
	function distribute(uint256 _amount) public {
    require(msg.sender == address(insurancePool), "Not insurance pool");
		uint256 length = poolInfoPidList.length;
		for (uint256 i = 0; i < length; ++i) {
			updatePool(poolInfoPidList[i], _amount);
		}
	}

	// Update reward variables of the given pool to be up-to-date.
	function updatePool(uint256 _pid, uint256 _amount) public {
		PoolInfo storage pool = poolInfo[_pid];
		if (block.timestamp <= pool.lastRewardTimestamp) {
			return;
		}
		uint256 lpSupply = pool.lpToken.balanceOf(address(this));
		if (lpSupply == 0 || pool.allocPoint == 0) {
			pool.lastRewardTimestamp = block.timestamp;
			return;
		}
		pool.accRewardPerShare += ((pool.allocPoint * _amount * 1e12) / totalAllocPoint / lpSupply);
		pool.lastRewardTimestamp = block.timestamp;
	}

	// Deposit LP tokens for reward allocation.
	function deposit(uint256 _pid, uint256 _amount) public nonReentrant {
		PoolInfo storage pool = poolInfo[_pid];
		UserInfo storage user = userInfo[_pid][msg.sender];

		uint256 pending = ((user.amount * pool.accRewardPerShare) / 1e12) - user.rewardDebt;
		if (pending > 0) {
			safeRewardTransfer(msg.sender, pending);
		}

		if (_amount > 0) {
			pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
			if (pool.depositFeeBP > 0) {
				uint256 depositFee = (_amount * pool.depositFeeBP) / 10000;
				pool.lpToken.safeTransfer(feeAddress, depositFee);
				user.amount = (user.amount + _amount) - depositFee;
			} else {
				user.amount = user.amount + _amount;
			}
		}
		user.rewardDebt = (user.amount * pool.accRewardPerShare) / 1e12;
		emit Deposit(msg.sender, _pid, _amount);
	}

	function harvestAll() external {
		uint256 length = poolInfoDummyList.length;
		for (uint256 pid = 0; pid < length; ++pid) {
			deposit(pid, 0);
		}
	}

	function harvestMany(uint256[] memory pids) external {
		uint256 length = pids.length;
		for (uint256 i = 0; i < length; ++i) {
			deposit(pids[i], 0);
		}
	}

	// Withdraw LP tokens.
	function withdraw(uint256 _pid, uint256 _amount) external nonReentrant {
		PoolInfo storage pool = poolInfo[_pid];
		UserInfo storage user = userInfo[_pid][msg.sender];
		require(user.amount >= _amount, "invalid amount");

		uint256 pending = ((user.amount * pool.accRewardPerShare) / 1e12) - user.rewardDebt;
		if (pending > 0) {
			safeRewardTransfer(msg.sender, pending);
		}
		if (_amount > 0) {
			user.amount = user.amount - _amount;
			pool.lpToken.safeTransfer(address(msg.sender), _amount);
		}
		user.rewardDebt = (user.amount * pool.accRewardPerShare) / 1e12;
		emit Withdraw(msg.sender, _pid, _amount);
	}

	// Withdraw without caring about rewards. EMERGENCY ONLY.
	function emergencyWithdraw(uint256 _pid) external nonReentrant {
		PoolInfo storage pool = poolInfo[_pid];
		UserInfo storage user = userInfo[_pid][msg.sender];
		uint256 amount = user.amount;
		user.amount = 0;
		user.rewardDebt = 0;
		pool.lpToken.safeTransfer(address(msg.sender), amount);
		emit EmergencyWithdraw(msg.sender, _pid, amount);
	}

	// Safe reward transfer function, just in case if rounding error causes pool to not have enough reward.
	function safeRewardTransfer(address _to, uint256 _amount) internal {
		uint256 rewardBal = baseToken.balanceOf(address(this));

		if (_amount > rewardBal) {
			baseToken.safeTransfer(_to, rewardBal);
		} else {
			baseToken.safeTransfer(_to, _amount);
		}
	}

	function setFeeAddress(address _feeAddress) external onlyOwner {
		feeAddress = _feeAddress;
		emit SetFeeAddress(msg.sender, _feeAddress);
	}
}

contracts/InsuranceNft.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./InsurancePool.sol";
import "./IDevParameters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "./meta-transactions/EIP712Base.sol";

error TokenIdUsed(uint256 tokenId);
error TokenNotExists(uint256 tokenId);
error NotOperator(address wallet);
error OnlyOwner();
error NonceReused(uint256 nonce);
error TooMuchFee(uint256 fee);
error InvalidMessageHash();
error InvalidTypeHash();

contract InsuranceNft is ERC721EnumerableUpgradeable, EIP712Base {
  using SafeERC20 for IERC20;
  
  InsurancePool public pool;
  IERC20 public baseToken;

  string _name;
  string _symbol;

  mapping(address => bool) public operators;
  mapping(uint256 => bytes) public tokenMessage;
  mapping(uint256 => uint256) public tokenExpiration;
  mapping(uint256 => mapping(uint256 => bool)) public tokenNonce;

  mapping(uint256 => address) public tokenReferralAddress;
  mapping(uint256 => uint256) public tokenReferralPercentage;

  // in BPS unit
  uint256 private buyerDevFee;

  // EIP-712 typehash
  bytes32 private constant BUY_TYPEHASH = keccak256(
    bytes(
      "BuyInsurance(address to,uint256 price,uint256 tokenId,uint256 duration,address referral,uint256 referralPercentage,uint256 expiration,bytes message)"
    )
  );

  bytes32 private constant EXTEND_TYPEHASH = keccak256(
    bytes(
      "ExtendInsurance(uint256 price,uint256 tokenId,uint256 nonce,uint256 duration,uint256 expiration,bytes message)"
    )
  );

  bytes32 private constant CLAIM_TYPEHASH = keccak256(
    bytes(
      "Claim(address to,uint256 amount,uint256 tokenId,uint256 nonce,uint256 expiration,bytes message)"
    )
  );

  /**
   * @dev Tells the address of the owner
   * @return the address of the owner
   */
  function owner() public view returns(address) {
    return pool.owner();
  }

  modifier onlyOwner() {
    require(pool.isGovernance(msg.sender), "Ownable: caller is not the owner");
    _;
  }

  function initialize(
    address _pool,
    IERC20 _baseToken,
    string memory _tokenName,
    string memory _tokenSymbol
  ) external initializer {
    buyerDevFee = 10001;
    pool = InsurancePool(_pool);
    baseToken = _baseToken;
    __ERC721_init(_tokenName, _tokenSymbol);
    _initializeEIP712("InsuranceNft");
  }

  function getMaxBuyerFee() public view returns (uint256) {
    return IDevParameters(pool.factory()).maxBuyerFee();
  }

  function getBuyerDevFee() public view returns (uint256) {
    if (buyerDevFee == 10001) {
      return IDevParameters(pool.factory()).buyerDevFee();
    }
    return buyerDevFee;
  }

  event UpdateBuyerDevFee(uint256 newFeeBps);
  function updateBuyerDevFee(uint256 feeBps) public {
    require(msg.sender == OwnableUpgradeable(pool.factory()).owner(), "Not dev");
    // Limit to maxBuyerFee
    require(feeBps <= getMaxBuyerFee() || feeBps == 10001, "Invalid fee");
    buyerDevFee = feeBps;
    emit UpdateBuyerDevFee(feeBps);
  }

  event SetOperator(address indexed operator, bool enabled);
  function setOperator(address operator, bool enabled) public onlyOwner {
    operators[operator] = enabled;
    emit SetOperator(operator, enabled);
  }

  event BuyInsurance(address indexed payer, address indexed buyer, uint256 indexed tokenId, uint256 price, uint256 tokenExpiration, bytes message);
  function buyInsurance(bytes calldata data, bytes memory message, bytes calldata signature) public {
    (bytes32 typeHash, address to, uint256 price, uint256 tokenId, uint256 tokenExpirationValue, address referral, uint256 referralPercentage, uint256 expiration, bytes32 messageHash) = abi.decode(data, (bytes32, address, uint256, uint256, uint256, address, uint256, uint256, bytes32));

    if (typeHash != BUY_TYPEHASH) revert InvalidTypeHash();
    if (keccak256(message) != messageHash) revert InvalidMessageHash();
    if (_exists(tokenId)) revert TokenIdUsed(tokenId);
    if (block.timestamp > expiration) revert Expired(expiration);

    address operator = ECDSA.recover(toTypedMessageHash(keccak256(data)), signature);

    if (!operators[operator]) revert NotOperator(operator);

    uint256 ownerFeeBps = pool.buyPercentageFee();
    uint256 buyerDevFeeBps = getBuyerDevFee();
    uint256 maxBuyerFeeBps = getMaxBuyerFee();

    // if (buyerDevFeeBps + referralPercentage > getMaxBuyerFee()) revert TooMuchFee(buyerDevFeeBps + referralPercentage);
    if (ownerFeeBps + buyerDevFeeBps + referralPercentage > maxBuyerFeeBps) {
      referralPercentage = maxBuyerFeeBps - buyerDevFee - ownerFeeBps;
    }

    {
      uint256 ownerFee = price * ownerFeeBps / 10000;
      uint256 devFee = price * buyerDevFeeBps / 10000;
      uint256 referralFee = price * referralPercentage / 10000;

      baseToken.safeTransferFrom(msg.sender, pool.owner(), ownerFee);
      baseToken.safeTransferFrom(msg.sender, OwnableUpgradeable(pool.factory()).owner(), devFee);
      baseToken.safeTransferFrom(msg.sender, referral, referralFee);
      baseToken.safeTransferFrom(msg.sender, address(pool), price - ownerFee - devFee - referralFee);
      pool.distribute(price - ownerFee - devFee - referralFee);
    }

    _mint(to, tokenId);
    tokenMessage[tokenId] = message;
    tokenExpiration[tokenId] = tokenExpirationValue;
    tokenReferralAddress[tokenId] = referral;
    tokenReferralPercentage[tokenId] = referralPercentage;

    emit BuyInsurance(msg.sender, to, tokenId, price, tokenExpirationValue, message);
  }

  event ExtendInsurance(address indexed payer, uint256 indexed tokenId, uint256 price, uint256 tokenExpiration, bytes message);
  function extendInsurance(bytes calldata data, bytes memory message, bytes calldata signature) public {
    (bytes32 typeHash, uint256 price, uint256 tokenId, uint256 nonce, uint256 tokenExpirationValue, uint256 expiration, bytes32 messageHash) = abi.decode(data, (bytes32, uint256, uint256, uint256, uint256, uint256, bytes32));

    if (typeHash != EXTEND_TYPEHASH) revert InvalidTypeHash();
    if (keccak256(message) != messageHash) revert InvalidMessageHash();
    if (!_exists(tokenId)) revert TokenNotExists(tokenId);
    if (block.timestamp > expiration) revert Expired(expiration);
    if (tokenNonce[tokenId][nonce]) revert NonceReused(nonce);

    address operator = ECDSA.recover(toTypedMessageHash(keccak256(data)), signature);

    if (!operators[operator]) revert NotOperator(operator);

    uint256 referralPercentage = tokenReferralPercentage[tokenId];

    uint256 ownerFeeBps = pool.buyPercentageFee();
    uint256 buyerDevFeeBps = getBuyerDevFee();
    uint256 maxBuyerFeeBps = getMaxBuyerFee();

    if (ownerFeeBps + buyerDevFeeBps + referralPercentage > maxBuyerFeeBps) {
      referralPercentage = maxBuyerFeeBps - buyerDevFee - ownerFeeBps;
    }

    {
      uint256 ownerFee = price * ownerFeeBps / 10000;
      uint256 devFee = price * buyerDevFeeBps / 10000;
      uint256 referralFee = price * referralPercentage / 10000;

      baseToken.safeTransferFrom(msg.sender, pool.owner(), ownerFee);
      baseToken.safeTransferFrom(msg.sender, OwnableUpgradeable(pool.factory()).owner(), devFee);
      baseToken.safeTransferFrom(msg.sender, tokenReferralAddress[tokenId], referralFee);
      baseToken.safeTransferFrom(msg.sender, address(pool), price - ownerFee - devFee - referralFee);
      pool.distribute(price - ownerFee - devFee - referralFee);
    }

    tokenMessage[tokenId] = message;
    if (tokenExpirationValue > tokenExpiration[tokenId]) {
      tokenExpiration[tokenId] = tokenExpirationValue;
    }

    tokenNonce[tokenId][nonce] = true;

    emit ExtendInsurance(msg.sender, tokenId, price, tokenExpirationValue, message);
  }

  event Claim(address indexed to, uint256 amount, bytes message);
  function claim(bytes calldata data, bytes memory message, bytes calldata signature) public {
    (bytes32 typeHash, address to, uint256 amount, uint256 tokenId, uint256 nonce, uint256 expiration, bytes32 messageHash) = abi.decode(data, (bytes32, address, uint256, uint256, uint256, uint256, bytes32));
    
    if (typeHash != CLAIM_TYPEHASH) revert InvalidTypeHash();
    if (keccak256(message) != messageHash) revert InvalidMessageHash();

    // Relax this condition in case user request for claim on last day
    // if (block.timestamp > tokenExpiration[tokenId]) revert Expired(tokenExpiration[tokenId]);

    if (block.timestamp > expiration) revert Expired(expiration);
    if (tokenNonce[tokenId][nonce]) revert NonceReused(nonce);
    
    address operator = ECDSA.recover(toTypedMessageHash(keccak256(data)), signature);
    if (!operators[operator]) revert NotOperator(operator);

    pool.claim(to, amount);
    tokenNonce[tokenId][nonce] = true;

    emit Claim(to, amount, message);
  }

  function exists(uint256 tokenId) public view returns (bool) {
    return _exists(tokenId);
  }

  /**
    * @dev Returns the name of the token.
    */
  function name() public override view virtual returns (string memory) {
    return _name;
  }

  /**
    * @dev Returns the symbol of the token, usually a shorter version of the
    * name.
    */
  function symbol() public override view virtual returns (string memory) {
    return _symbol;
  }

  function _transfer(
    address from,
    address to,
    uint256 tokenId
  ) internal virtual override {
    revert("Soulbound Token");
  }

  /**
    * @dev Base URI for computing {tokenURI}. If set, the resulting URI for each
    * token will be the concatenation of the `baseURI` and the `tokenId`. Empty
    * by default, can be overridden in child contracts.
    */
  function _baseURI() internal view virtual override returns (string memory) {
    return string.concat("https://insurance.838earth.finance/api/tokens/", Strings.toHexString(uint256(uint160(msg.sender)), 20), "/");
  }
}

contracts/InsurancePool.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./IDistribute.sol";
import "./IDevParameters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

error InvalidPercentage();
error Zero();
error Expired(uint256 expiration);
error NotExpired(uint256 expiration);
error DepositLimit();
error OutOfReserve();
error WithdrawBug();

interface INftInitializer {
  function initialize(
    address _pool,
    IERC20 _baseToken,
    string memory _tokenName,
    string memory _tokenSymbol
  ) external;
}

interface IMasterchefInitializer is IDistribute {
	function initialize(
    address _insurancePool,
		address _baseToken
	) external;
}

contract InsurancePool is ERC20Upgradeable, OwnableUpgradeable, IDistribute {
  using SafeERC20 for IERC20;

  address public immutable nftImplementation;
  address public immutable masterchefImplementation;

  IERC20 public baseToken;

  address public factory;
  address public nft;
  IMasterchefInitializer public masterchef;

  uint256 public rewardPerShare;
  mapping(address => uint256) public rewardDebt; 

  uint256 public buyPercentageFee;
  uint256 public depositPercentageFee;
  uint256 public depositLimit;

  uint256 public distributionRatio;
  uint256 public masterchefRatio;

  uint256 public expiration;

  // This track profit of token stored in the insurance
  int256 public keepProfit;

  // in BPS unit
  uint256 private investorDevFee;

  uint256 public supplyAtExpired;

  modifier onlyNft {
    // TODO: HACKATHON only: Remove security for time trade off
    // require(msg.sender == nft, "Not NFT");
    _;
  }

  function isGovernance(address addr) public view returns(bool) {
    return addr == owner() || addr == OwnableUpgradeable(factory).owner();
  }

  modifier onlyGovernance {
    require(isGovernance(msg.sender), "Ownable: caller is not the owner");
    _;
  }

  constructor(
    address _nftImplementation,
    address _masterchefImplementation
  ) {
    nftImplementation = _nftImplementation;
    masterchefImplementation = _masterchefImplementation;
  }

  function getInvestorDevFee() public view returns(uint256) {
    if (investorDevFee == 10001) {
      return IDevParameters(factory).investorDevFee();
    }
    return investorDevFee;
  }

  event UpdateInvestorDevFee(uint256 newFeeBps);
  function updateInvestorDevFee(uint256 feeBps) public {
    require(msg.sender == OwnableUpgradeable(factory).owner(), "Not dev");
    require(feeBps <= 10001, "Invalid fee");
    investorDevFee = feeBps;
    emit UpdateInvestorDevFee(feeBps);
  }

  event PoolInitialize(address indexed owner, address indexed nftContract, address indexed masterchefContract, uint256 expiration);
  function initialize(
    address _owner,
    IERC20 _baseToken,
    uint256 _buyPercentageFee,
    uint256 _depositPercentageFee,
    uint256 _depositLimit,
    uint256 _distributionRatio,
    uint256 _masterchefRatio,
    uint256 _expiration,
    string memory _tokenName,
    string memory _tokenSymbol
  ) external initializer {
    if (!(_distributionRatio <= 10000 && _masterchefRatio <= 10000 && _depositPercentageFee <= 10000 && 10000 - _distributionRatio - _masterchefRatio >= 0)) {
      revert InvalidPercentage();
    }

    investorDevFee = 10001;
    
    factory = msg.sender;

    if (IDevParameters(factory).buyerDevFee() + _buyPercentageFee > IDevParameters(factory).maxBuyerFee()) {
      revert InvalidPercentage();
    }

    nft = Clones.clone(nftImplementation);
    INftInitializer(nft).initialize(address(this), _baseToken, _tokenName, _tokenSymbol);

    masterchef = IMasterchefInitializer(Clones.clone(masterchefImplementation));
    masterchef.initialize(address(this), address(_baseToken));

    baseToken = _baseToken;

    buyPercentageFee = _buyPercentageFee;
    depositPercentageFee = _depositPercentageFee;
    depositLimit = _depositLimit;

    distributionRatio = _distributionRatio;
    masterchefRatio = _masterchefRatio;

    expiration = _expiration;

    _transferOwnership(_owner);
    __ERC20_init(_tokenName, _tokenSymbol);

    emit PoolInitialize(_owner, address(nft), address(masterchef), _expiration);
  }

  function getParameters() public view returns(
    address _owner,
    IERC20 _baseToken,
    uint256 _buyPercentageFee,
    uint256 _depositPercentageFee,
    uint256 _depositLimit,
    uint256 _distributionRatio,
    uint256 _masterchefRatio,
    uint256 _expiration,
    string memory _tokenName,
    string memory _tokenSymbol
  ) {
    _owner = owner();
    _baseToken = baseToken;
    _buyPercentageFee = buyPercentageFee;
    _depositPercentageFee = depositPercentageFee;
    _depositLimit = depositLimit;
    _distributionRatio = distributionRatio;
    _masterchefRatio = masterchefRatio;
    _expiration = expiration;
    _tokenName = name();
    _tokenSymbol = symbol();
  }

  event SetDepositLimit(uint256 newLimit);
  function setDepositLimit(uint256 newLimit) public onlyGovernance {
    depositLimit = newLimit;
    emit SetDepositLimit(newLimit);
  }

  event SetBuyPercentageFee(uint256 newPercentageFee);
  function setBuyPercentageFee(uint256 newPercentageFee) public onlyGovernance {
    if (IDevParameters(factory).buyerDevFee() + newPercentageFee > IDevParameters(factory).maxBuyerFee()) {
      revert InvalidPercentage();
    }
    buyPercentageFee = newPercentageFee;
    emit SetBuyPercentageFee(newPercentageFee);
  }

  event SetDepositPercentageFee(uint256 newPercentageFee);
  function setDepositPercentageFee(uint256 newPercentageFee) public onlyGovernance {
    if (newPercentageFee > 10000) revert InvalidPercentage();
    depositPercentageFee = newPercentageFee;
    emit SetDepositPercentageFee(newPercentageFee);
  }

  event SetDistributionRatio(uint256 newRatio);
  function setDistributionRatio(uint256 newRatio) public onlyGovernance {
    if (newRatio > 10000) revert InvalidPercentage();
    distributionRatio = newRatio;
    emit SetDistributionRatio(newRatio);
  }

  event SetMasterchefRatio(uint256 newRatio);
  function setMasterchefRatio(uint256 newRatio) public onlyGovernance {
    if (newRatio > 10000) revert InvalidPercentage();
    masterchefRatio = newRatio;
    emit SetMasterchefRatio(newRatio);
  }

  function getPendingReward(address wallet) public view returns(uint256) {
    uint256 rewardFromShare = rewardPerShare * balanceOf(wallet) / 1e18;
    uint256 amount = rewardFromShare - rewardDebt[wallet];
    return amount;
  }

  event Harvest(address to, uint256 amount);
  function harvest(address to) public {
    if (to != address(0)) {
      uint256 rewardFromShare = rewardPerShare * balanceOf(to) / 1e18;
      uint256 amount = rewardFromShare - rewardDebt[to];
      baseToken.safeTransfer(to, amount);
      rewardDebt[to] = rewardFromShare;
      emit Harvest(to, amount);
    }
  }

  event Deposit(address indexed payer, address indexed to, uint256 amount, uint256 fee);
  function deposit(address to, uint256 amount) public {
    if (amount == 0) revert Zero();
    if (expiration != 0 && block.timestamp > expiration) revert Expired(expiration);

    // Note: To pause deposit, set deposit limit to <= total supply but not zero
    if (depositLimit != 0 && depositLimit + amount > totalSupply()) revert DepositLimit();

    harvest(to);

    baseToken.safeTransferFrom(msg.sender, address(this), amount);

    uint256 fee = amount * depositPercentageFee * (10000 - getInvestorDevFee()) / 1e8;
    uint256 devFee = amount * depositPercentageFee * getInvestorDevFee() / 1e8;

    baseToken.safeTransfer(owner(), fee);
    baseToken.safeTransfer(OwnableUpgradeable(factory).owner(), devFee);

    _mint(to, amount - fee - devFee);

    rewardDebt[to] = rewardPerShare * balanceOf(to) / 1e18;

    emit Deposit(msg.sender, to, amount, fee);
  }

  event Distribute(uint256 amount, int256 keepProfit, uint256 rewardPerShare);
  function distribute(uint256 amount) public onlyNft {
    // NFT contract is responsible for transferring base amount

    if (amount == 0) revert Zero();
    if (expiration != 0 && block.timestamp > expiration) revert Expired(expiration);

    uint256 distributeAmount = amount * distributionRatio / 10000;

    keepProfit += int256(amount - distributeAmount);

    if (masterchefRatio > 0) {
      uint256 masterchefAmount = amount * masterchefRatio / 10000;
      baseToken.safeTransfer(address(masterchef), masterchefAmount);
      masterchef.distribute(masterchefAmount);
      keepProfit -= int256(masterchefAmount);
    }

    if (totalSupply() == 0) {
      // Refund distributeAmount back to the pool
      keepProfit += int256(distributeAmount);
    } else {
      rewardPerShare += distributeAmount * 1e18 / totalSupply();
    }

    emit Distribute(amount, keepProfit, rewardPerShare);
  }

  event Claim(uint256 amount, int256 keepProfit);
  function claim(address to, uint256 amount) public onlyNft {
    if (amount == 0) revert Zero();
    if (expiration != 0 && block.timestamp > expiration) revert Expired(expiration);

    keepProfit -= int256(amount);
    if (keepProfit < -int256(totalSupply())) revert OutOfReserve();
    baseToken.safeTransfer(to, amount);
    emit Claim(amount, keepProfit);
  }

  event Redeem(address indexed redeemer, uint256 amount, uint256 total);
  function redeem(uint256 amount) public {
    if (amount == 0) revert Zero();
    // If no expiration -> can't redeem
    if (expiration == 0 || block.timestamp <= expiration) revert NotExpired(expiration);

    if (supplyAtExpired == 0) {
      supplyAtExpired = totalSupply();
    }

    int256 supply = int256(supplyAtExpired);
    int256 totalInt = (supply + keepProfit) * int256(amount) / supply;

    if (totalInt <= 0) {
      revert WithdrawBug();
    }
    
    uint256 total = uint256(totalInt);

    _burn(msg.sender, amount);
    baseToken.safeTransfer(msg.sender, total);

    emit Redeem(msg.sender, amount, total);
  }

  // Take loss but not take profit
  event Withdraw(address indexed withdrawer, uint256 amount, uint256 total);
  function withdraw(uint256 amount) public {
    if (amount == 0) revert Zero();
    // If expired -> use redeem instead
    if (expiration != 0 && block.timestamp > expiration) revert Expired(expiration);

    int256 supply = int256(totalSupply());
    int256 totalInt = keepProfit >= 0 ? int256(amount) : (supply + keepProfit) * int256(amount) / supply;

    if (totalInt <= 0) {
      revert WithdrawBug();
    }

    keepProfit += int256(amount) - totalInt;

    uint256 total = uint256(totalInt);

    _burn(msg.sender, amount);
    baseToken.safeTransfer(msg.sender, total);

    emit Withdraw(msg.sender, amount, total);
  }

  function _beforeTokenTransfer(
    address from,
    address to,
    uint256
  ) internal virtual override {
    harvest(from);
    harvest(to);
  }

  function _afterTokenTransfer(
    address from,
    address to,
    uint256
  ) internal virtual override {
    rewardDebt[from] = rewardPerShare * balanceOf(from) / 1e18;
    rewardDebt[to] = rewardPerShare * balanceOf(to) / 1e18;
  }
}

contracts/meta-transactions/ContextMixin.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

abstract contract ContextMixin {
    function msgSender() internal view returns (address sender) {
        if (msg.sender == address(this)) {
            bytes memory array = msg.data;
            uint256 index = msg.data.length;
            assembly {
                // Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
                sender := and(
                    mload(add(array, index)),
                    0xffffffffffffffffffffffffffffffffffffffff
                )
            }
        } else {
            sender = msg.sender;
        }
        return sender;
    }
}

contracts/meta-transactions/EIP712Base.s.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract EIP712Base is Initializable {
    struct EIP712Domain {
        string name;
        string version;
        address verifyingContract;
        bytes32 salt;
    }

    string constant public ERC712_VERSION = "1";

    bytes32 internal constant EIP712_DOMAIN_TYPEHASH = keccak256(
        bytes(
            "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
        )
    );
    bytes32 internal domainSeperator;

    // supposed to be called once while initializing.
    // one of the contracts that inherits this contract follows proxy pattern
    // so it is not possible to do this in a constructor
    function _initializeEIP712(
        string memory name
    )
        internal
        onlyInitializing
    {
        _setDomainSeperator(name);
    }

    function _setDomainSeperator(string memory name) internal {
        domainSeperator = keccak256(
            abi.encode(
                EIP712_DOMAIN_TYPEHASH,
                keccak256(bytes(name)),
                keccak256(bytes(ERC712_VERSION)),
                getChainId(),
                address(this)
            )
        );
    }

    function getDomainSeperator() public view returns (bytes32) {
        return domainSeperator;
    }

    function getChainId() public view returns (uint256) {
        uint256 id;
        assembly {
            id := chainid()
        }
        return id;
    }

    /**
     * Accept message hash and returns hash message in EIP712 compatible form
     * So that it can be used to recover signer from signature signed using EIP712 formatted data
     * https://eips.ethereum.org/EIPS/eip-712
     * "\\x19" makes the encoding deterministic
     * "\\x01" is the version byte to make it compatible to EIP-191
     */
    function toTypedMessageHash(bytes32 messageHash)
        internal
        view
        returns (bytes32)
    {
        return
            keccak256(
                abi.encodePacked("\x19\x01", getDomainSeperator(), messageHash)
            );
    }
}

contracts/optidomains/AttestationStation.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title Semver
 * @notice Semver is a simple contract for managing contract versions.
 */
contract Semver {
    /**
     * @notice Contract version number (major).
     */
    uint256 private immutable MAJOR_VERSION;

    /**
     * @notice Contract version number (minor).
     */
    uint256 private immutable MINOR_VERSION;

    /**
     * @notice Contract version number (patch).
     */
    uint256 private immutable PATCH_VERSION;

    /**
     * @param _major Version number (major).
     * @param _minor Version number (minor).
     * @param _patch Version number (patch).
     */
    constructor(
        uint256 _major,
        uint256 _minor,
        uint256 _patch
    ) {
        MAJOR_VERSION = _major;
        MINOR_VERSION = _minor;
        PATCH_VERSION = _patch;
    }

    /**
     * @notice Returns the full semver contract version.
     *
     * @return Semver contract version as a string.
     */
    function version() public view returns (string memory) {
        return
            string(
                abi.encodePacked(
                    Strings.toString(MAJOR_VERSION),
                    ".",
                    Strings.toString(MINOR_VERSION),
                    ".",
                    Strings.toString(PATCH_VERSION)
                )
            );
    }
}

/**
 * @title AttestationStation
 * @author Optimism Collective
 * @author Gitcoin
 * @notice Where attestations live.
 */
contract AttestationStation is Semver {
    /**
     * @notice Struct representing data that is being attested.
     *
     * @custom:field about Address for which the attestation is about.
     * @custom:field key   A bytes32 key for the attestation.
     * @custom:field val   The attestation as arbitrary bytes.
     */
    struct AttestationData {
        address about;
        bytes32 key;
        bytes val;
    }

    /**
     * @notice Maps addresses to attestations. Creator => About => Key => Value.
     */
    mapping(address => mapping(address => mapping(bytes32 => bytes))) public attestations;

    /**
     * @notice Emitted when Attestation is created.
     *
     * @param creator Address that made the attestation.
     * @param about   Address attestation is about.
     * @param key     Key of the attestation.
     * @param val     Value of the attestation.
     */
    event AttestationCreated(
        address indexed creator,
        address indexed about,
        bytes32 indexed key,
        bytes val
    );

    /**
     * @custom:semver 1.1.0
     */
    constructor() Semver(1, 1, 0) {}

    /**
     * @notice Allows anyone to create an attestation.
     *
     * @param _about Address that the attestation is about.
     * @param _key   A key used to namespace the attestation.
     * @param _val   An arbitrary value stored as part of the attestation.
     */
    function attest(
        address _about,
        bytes32 _key,
        bytes memory _val
    ) public {
        attestations[msg.sender][_about][_key] = _val;

        emit AttestationCreated(msg.sender, _about, _key, _val);
    }

    /**
     * @notice Allows anyone to create attestations.
     *
     * @param _attestations An array of attestation data.
     */
    function attest(AttestationData[] calldata _attestations) external {
        uint256 length = _attestations.length;
        for (uint256 i = 0; i < length; ) {
            AttestationData memory attestation = _attestations[i];

            attest(attestation.about, attestation.key, attestation.val);

            unchecked {
                ++i;
            }
        }
    }
}

contracts/optidomains/DiamondResolverUti.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.8;

import {OwnableStorage} from "@solidstate/contracts/access/ownable/OwnableStorage.sol";
import "./INameWrapperRegistry.sol";
import "./OptiDomainsAttestation.sol";

error NotDiamondOwner();

interface IVersionableResolver {
    // event VersionChanged(bytes32 indexed node, uint64 newVersion);

    function recordVersions(bytes32 node) external view returns (uint64);
    function clearRecords(bytes32 node) external;
}

/**
 * @dev derived from https://github.com/mudgen/diamond-2 (MIT license)
 */
library DiamondBaseStorage {
    struct Layout {
        // function selector => (facet address, selector slot position)
        mapping(bytes4 => bytes32) facets;
        // total number of selectors registered
        uint16 selectorCount;
        // array of selector slots with 8 selectors per slot
        mapping(uint256 => bytes32) selectorSlots;
        address fallbackAddress;
    }

    bytes32 internal constant STORAGE_SLOT =
        keccak256('solidstate.contracts.storage.DiamondBase');

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

abstract contract DiamondResolverUtil {
    error Unauthorised();

    event VersionChanged(bytes32 indexed node, uint64 newVersion);

    modifier baseOnlyOwner() {
        if (msg.sender != OwnableStorage.layout().owner) revert NotDiamondOwner();
        _;
    }

    function _registry() internal view returns(INameWrapperRegistry) {
        return IHasNameWrapperRegistry(address(this)).registry();
    }

    function _attestation() internal view returns(OptiDomainsAttestation) {
        return OptiDomainsAttestation(_registry().attestation());
    }

    function _readAttestation(bytes32 node, bytes32 key) internal view returns(bytes memory) {
        return _attestation().readAttestation(node, key);
    }

    function _attest(bytes32 node, bytes32 key, bytes memory value) internal {
        _attestation().attest(node, key, value);
    }

    function _recordVersions(bytes32 node) internal view returns (uint64) {
        return _attestation().readVersion(node);
    }

    /**
     * Increments the record version associated with an ENS node.
     * May only be called by the owner of that node in the ENS registry.
     * @param node The node to update.
     */
    function _clearRecords(bytes32 node) internal virtual {
        _attestation().increaseVersion(node);
        emit VersionChanged(node, _recordVersions(node));
    }

    function _isAuthorised(bytes32 node) internal view returns (bool) {
        (bool success, bytes memory result) = address(this).staticcall(
            abi.encodeWithSelector(0x25f36704, msg.sender, node)
        );
        if (!success) return false;
        return abi.decode(result, (bool));
    }

    modifier authorised(bytes32 node) {
        if (!_isAuthorised(node)) revert Unauthorised();
        _;
    }
}

